namespace UI
{
    using BE.Entidades;
    using BLL;
    using DAL.Dao;
    using System;
    using System.Collections.Generic;
    using System.Linq;
    using System.Resources;
    using System.Windows.Forms;

    public partial class Login : Form
    {

        private log4net.ILog log;
        private IPrincipal PrincipalForm;
        private IUsuarioBLL usuarioBLL;
        private IFormControl formControl;
        private readonly IIdiomaBLL idiomaBLL;
        private readonly IDigitoVerificador digitoVerificador;

        public Login(IIdiomaBLL idiomaBLL, IDigitoVerificador digitoVerificador)
        {
            this.digitoVerificador = digitoVerificador;
            this.idiomaBLL = idiomaBLL;
            InitializeComponent();
            txt_contraseña.PasswordChar = '*';
        }


        private void Form1_Load(object sender, EventArgs e)
        {
            LimpiarRecursos();
            this.AcceptButton = btn_ingresar;
            log = log4net.LogManager.GetLogger(System.Reflection.MethodBase.GetCurrentMethod().DeclaringType);
            PrincipalForm = IoCContainer.Resolve<IPrincipal>();
            usuarioBLL = IoCContainer.Resolve<IUsuarioBLL>();
            formControl = IoCContainer.Resolve<IFormControl>();
            CargarCombo();
            formControl.LenguajeSeleccionado = (Idioma)cbo_idioma.SelectedItem;
            Traduccir();
            ////ComprobarBaseDeDatos();
        }

        private void ComprobarBaseDeDatos()
        {
            if (!digitoVerificador.ComprobarIntegridad())
            {
                ////traducir
                Alert.ShowSimpleAlert("Problema integridad base de datos, contacte al administrador", "MSJ000");

                this.Close();

            }
        }

        private void Traduccir()
        {
            formControl.Traducciones.Clear();
            formControl.Traducciones = GetTraducciones();
            idiomaBLL.LlenarRecursos(formControl.Traducciones, formControl.LenguajeSeleccionado.IdIdioma, Application.OpenForms[0].Name);
            idiomaBLL.LeerRecursos(this.Controls);
        }

        private IDictionary<string, string> GetTraducciones()
        {
            formControl.Traducciones = idiomaBLL.ObtenerTraduccionesFormulario(formControl.LenguajeSeleccionado.IdIdioma, Application.OpenForms[0].Name).ToDictionary(k => k.ControlName ?? k.MensajeCodigo, v => v.Traduccion);

            return formControl.Traducciones;
        }

        private void CargarCombo()
        {
            var idioma = idiomaBLL.ObtenerTodosLosIdiomas();
            cbo_idioma.DataSource = idioma;
            cbo_idioma.ValueMember = "IdIdioma";
            cbo_idioma.DisplayMember = "Descripcion";
            cbo_idioma.SelectedIndex = 0;
        }

        private void btn_ingresar_Click(object sender, EventArgs e)
        {
            this.CatchException(() =>
            {
                var usuario = txt_user.Text;
                var contraseña = txt_contraseña.Text;
                ////admin123
                var ingresa = usuarioBLL.LogIn(usuario, contraseña);

                if (ingresa)
                {
                    this.Hide();
                    formControl.GuardarDatosSesionUsuario(usuarioBLL.ObtenerUsuarioConEmail(usuario));
                    PrincipalForm.ComprobarSiEsPrimerLogin(usuario);
                    PrincipalForm.Show();
                }
                else
                {
                    MessageBox.Show("Login Incorrecto");
                }
            },
            (ex) => MessageBox.Show($"Ocurrio un error por lo siguiente {ex.Message}"));
        }

        private void cbo_idioma_SelectedIndexChanged(object sender, EventArgs e)
        {
            var lenguajeSeleccionado = (Idioma)cbo_idioma.SelectedItem;

            formControl.LenguajeSeleccionado = lenguajeSeleccionado;

            using (ResXResourceWriter resxWriter = new ResXResourceWriter(formControl.ObtenerDirectorio()))
            {
                resxWriter.Dispose();
            }
            AplicarTraduccion();
        }

        private void btn_salir_Click(object sender, EventArgs e)
        {
            if (Alert.ConfirmationMessage("MSJ001", "Salir del sistema", MessageBoxButtons.YesNo) == DialogResult.Yes)
            {
                this.Close();
            }
        }

        public void AplicarTraduccion()
        {
            var provIdioma = FormExtensions.AplicarTraducciones();
            provIdioma.LeerRecursos(this.Controls);
        }

        private void LimpiarRecursos()
        {
            using (ResXResourceWriter resxWriter = new ResXResourceWriter("C:\\Users\\Nicolas Azzara\\Desktop\\diploma\\diploma\\SysAnalizer\\Recursos\\Español.resx"))
            {
                resxWriter.Dispose();
            }
        }
    }
}
