// <auto-generated/>
namespace UI
{
    using BLL;
    using System;
    using System.Windows.Forms;

    public partial class Login : Form
    {
        private IPrincipal PrincipalForm;
        private IUsuarioBLL usuarionBLL;

        public Login()
        {
            InitializeComponent();
        }

        log4net.ILog log;

        private void Form1_Load(object sender, EventArgs e)
        {
            log4net.Config.XmlConfigurator.Configure();
            log = log4net.LogManager.GetLogger(System.Reflection.MethodBase.GetCurrentMethod().DeclaringType);
            //PrincipalForm = Principal.GetInstancia();
            PrincipalForm = IoCContainer.Resolve<IPrincipal>();
            usuarionBLL = IoCContainer.Resolve<IUsuarioBLL>();
        }

        private void btn_ingresar_Click(object sender, EventArgs e)
        {
            this.CatchException(() =>
            {
                string usuario = txt_user.Text;
                string contraseña = txt_contraseña.Text;

                bool ingresa = usuarionBLL.LogIn(usuario, contraseña);

                if (ingresa)
                {
                    log.Info("Login Correcto de usuario");
                    this.Hide();
                    PrincipalForm.Show();
                }
                else
                {
                    log.Info("Login Incorrecto de usuario");
                    MessageBox.Show("Su cuenta ha sido bloqueada contacte al Administrador");
                }
            },
            (ex) => MessageBox.Show($"Exploto por lo siguiente {ex.Message}"));
        }

        private void cbo_idioma_SelectedIndexChanged(object sender, EventArgs e)
        {

        }

        private void btn_salir_Click(object sender, EventArgs e)
        {
            this.Close();
        }
    }
}
